//   AddRightClickMenu(), AttachInputFilters(), BuildKeypad(), StylePrimaryButton(),
//   StyleGhostButton(), SetSecondInputVisible() which live in Form1.cs.
// ============================================================

using System.ComponentModel;   // IContainer, Container
using System.Drawing;          // Font, Color, Size, Padding
using System.Windows.Forms;    // WinForms controls like TextBox, Button, TableLayoutPanel, etc.

namespace Freight_Cost
{
    partial class Form1
