        /// <summary>
        /// Required designer variable.
        /// This container holds components that need disposal (like ToolTips).
        /// </summary>
        private IContainer components = null;

        // ============================================================
        // Control Fields
        // ============================================================
        //
        // These are the UI elements on the form. They are fields so:
        // - Other code (Form1.cs) can access them
        // - Event handlers can reference them
        // - State can be read/updated after initialization
        //
        // Naming convention here uses underscores (private fields).
        // ============================================================

        private TextBox _input1;          // Quote input
        private TextBox _input2;          // Optional 2nd input (CH Robinson fee)
        private TextBox _output;          // (Not used in this snippet, but kept as a field)
        private Label _label1;            // Label for input1
        private Label _label2;            // Label for input2
        private Label _historyHeader;     // (Used in Form1.cs for header click behavior)
        private CheckBox _optA;           // Option A checkbox
        private CheckBox _optB;           // Option B checkbox
        private Button _calc;             // Calculate button
        private DataGridView _history;    // History table

        // Layout containers (TableLayoutPanels are easy for ‚Äúgrid‚Äù layouts)
        private TableLayoutPanel _split;       // 2-column split: history + calculator
        private TableLayoutPanel _left;        // Left side content
        private TableLayoutPanel _inputs;      // Contains labels + input textboxes
        private TableLayoutPanel _optionsRow;  // Contains checkboxes
        private TableLayoutPanel _bottomRow;   // Contains YouTube button area
        private TableLayoutPanel _right;       // Right side content

        private Button _ytButton;         // Small help button that opens YouTube
        private Label _historyTitle;      // Title label ‚ÄúHistory‚Äù
        private PictureBox _rambo;             // RAMBO

        /// <summary>
        /// Clean up resources being used.
        /// When the form closes, we dispose any components that were created.
        /// </summary>
        /// <param name="disposing">
        /// true if managed resources should be disposed; otherwise false
        /// </param>
        protected override void Dispose(bool disposing)
        {
            // If we're disposing and we have a component container, dispose it
            // (ToolTips and similar components live here)
            if (disposing && (components != null))
            {
                components.Dispose();
            }

            // Base class cleanup
            base.Dispose(disposing);
        }

        #region Windows Form Designer generated code

        /// <summary>
        /// Initializes all controls, sets properties, and builds the layout.
        /// This is called from the Form1 constructor BEFORE you start wiring events.
        /// </summary>
        private void InitializeComponent()
        {
            // Create the component container first
            components = new Container();

            // Instantiate UI controls (actual objects)
            _input1 = new TextBox();
            _input2 = new TextBox();
            _output = new TextBox();
            _label1 = new Label();
            _label2 = new Label();
            _historyHeader = new Label(); // NOTE: You are not adding this to the UI here;
                                          // it still exists as a field for the code-behind.
            _optA = new CheckBox();
            _optB = new CheckBox();
            _calc = new Button();
            _history = new DataGridView();

            // Instantiate layout containers
            _split = new TableLayoutPanel();
            _left = new TableLayoutPanel();
            _inputs = new TableLayoutPanel();
            _optionsRow = new TableLayoutPanel();
            _bottomRow = new TableLayoutPanel();
            _right = new TableLayoutPanel();

            // Other UI elements
            _ytButton = new Button();
            _rambo = new PictureBox();
            _historyTitle = new Label();

            // Pause layout calculations while we build UI
