            SuspendLayout();

            // ============================================================
            // APP WINDOW (Form-level settings)
            // ============================================================

            Text = "M.F. BOYS CALCULATOR";                  // Window title bar text
            Font = new Font("Segoe UI", 10f, FontStyle.Regular); // Default font for child controls
            StartPosition = FormStartPosition.CenterScreen; // Start centered on the screen
            FormBorderStyle = FormBorderStyle.FixedDialog;  // Fixed size dialog style window
            MaximizeBox = false;                            // Disable maximize button
            MinimizeBox = true;                            // Disable minimize button
            ClientSize = new Size(1000, 590);               // Window size (excluding title bar)
            BackColor = AppBackground;                      // Uses theme color from Form1.cs
            ForeColor = TextPrimary;                        // Uses theme color from Form1.cs
            AutoScaleMode = AutoScaleMode.Font;             // Automatically scales with font size

            // ============================================================
            // SPLIT LAYOUT: Left calculator + Right history
            // ============================================================
