            _split.Dock = DockStyle.Fill;        // Fill the whole form
            _split.ColumnCount = 2;              // Two columns
            _split.Padding = new Padding(12);    // Outer padding around the whole UI
            _split.BackColor = AppBackground;

            // ColumnStyles define width behavior for each column
            _split.ColumnStyles.Add(new ColumnStyle(SizeType.Absolute, 460)); // fixed
            _split.ColumnStyles.Add(new ColumnStyle(SizeType.Percent, 100));  // fill remainder

            // Add the split layout to the form
            Controls.Add(_split);

