            _inputs.RowStyles.Add(new RowStyle(SizeType.Absolute, 50)); // input1
            _inputs.RowStyles.Add(new RowStyle(SizeType.Absolute, 17)); // label2
            _inputs.RowStyles.Add(new RowStyle(SizeType.Absolute, 50)); // input2

            // Add inputs card into the left layout row 0
