            // White text makes the emoji stand out on dark background
            _ytButton.ForeColor = Color.White;

            // ToolTip component explains the button on hover
            var tip = new ToolTip();
            tip.SetToolTip(_ytButton, "Open help video (YouTube)");


            //_rambo.Image = Properties.Re
            //try
            //{
            //    if (System.IO.File.Exists("RAMBO.png"))
            //    {
            //        _rambo.Image = Image.FromFile("RAMBO.png");
            //    }
            //}
            //catch
            //{
            //    _rambo.Image = null; // or a default icon if you have one
            //}

            _rambo.Dock = DockStyle.None;
            _rambo.SizeMode = PictureBoxSizeMode.Zoom;
            _rambo.BackColor = AppBackground;



            _bottomRow.Controls.Add(_ytButton, 0, 0);
            _bottomRow.Controls.Add(new Panel { Dock = DockStyle.Fill, BackColor = AppBackground }, 1, 0);
            _bottomRow.Controls.Add(_rambo, 2, 0);

