            _right.Controls.Add(_history, 0, 1);

            // ============================================================
            // Final form tweaks
            // ============================================================

            // Apply theme again (harmless if already applied)
            ApplyModernTheme(this);

            // Pressing Enter triggers Calculate (nice UX)
            AcceptButton = _calc;

            // Resume layout now that controls are set up
            ResumeLayout(false);
